PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE preferences (    key VARCHAR PRIMARY KEY, value VARCHAR );
INSERT INTO preferences VALUES('session-key','8ead4ba9b4dd386e26339051678e3095e1da0a2dc66176ed');
CREATE TABLE threads (     id INTEGER PRIMARY KEY, uri VARCHAR(256) UNIQUE, title VARCHAR(256));
INSERT INTO threads VALUES(1,'/demo/index.html','Isso Test');
CREATE TABLE comments (     tid REFERENCES threads(id), id INTEGER PRIMARY KEY, parent INTEGER,     created FLOAT NOT NULL, modified FLOAT, mode INTEGER, remote_addr VARCHAR,     text VARCHAR, author VARCHAR, email VARCHAR, website VARCHAR,     likes INTEGER DEFAULT 0, dislikes INTEGER DEFAULT 0, voters BLOB NOT NULL,     notification INTEGER DEFAULT 0);
INSERT INTO comments VALUES(1,1,NULL,1651770353.67396545,NULL,1,'192.168.1.2',replace('Hi John, thank you for this wonderful article!\n\nHowever, I still struggle with *how* to apply the [thing you mentioned](https://example.org/thing) to my own use case. I''ve tried using the `malleable logarithmic casing` you suggested, but somehow my **Turbo encabulator** still experiences side fumbling.\n\nAny further ideas?','\n',char(10)),'Sue',NULL,'https://example.org',7,0,X'',0);
INSERT INTO comments VALUES(1,2,NULL,1651770409.02200174,NULL,1,'192.168.1.3','Great to see you writing again, John! Looking forward to meeting up again and sharing family pictures.','Angelo',NULL,'https://example.org',4,0,X'',0);
INSERT INTO comments VALUES(1,3,1,1651770506.47289681,NULL,1,'192.168.1.4',replace('Hey, I''m a bit privacy conscious so I commented anonymously - which works great, thanks!\n\nI struggle with a similar issue as Sue - could it be related to me only using five hydrocoptic marzelvanes?','\n',char(10)),NULL,NULL,NULL,1,0,X'',0);
INSERT INTO comments VALUES(1,4,1,1651770847.03000378,NULL,1,'192.168.1.5','Hi sue, hi anonymous commenter, great to hear you enjoyed my article. Have you ensured that the two spurving bearings are in a direct line with the `pentametric fan`?','John',NULL,'http://website.org',3,0,X'',0);
CREATE TRIGGER remove_stale_threads AFTER DELETE ON comments BEGIN     DELETE FROM threads WHERE id NOT IN (SELECT tid FROM comments); END;
COMMIT;
